<div style="border: 1px solid #D7BE4D; cursor: pointer;" data-down="%down%" data-up="%up%" data-lower="%lower%" data-closed="%closed%" data-ints="[%integers%]" style="cursor: pointer;" data-color="%color%" data-time1="%time1%" data-time2="%time2%" data-closed="%closed%" data-description="%description%" data-pos="%pos_box%" data-lasttime="%lasttime%" data-symbol="%symbol%" class="signal-mini notification notification-%pos% col-md-12">
    <div class="row">
        <div class="col-md-12"><span class="glyphicon glyphicon-arrow-%pos%"></span> %message%</div>
    </div>
</div>